async function addToArr(text){
	text = text.split(" ");

	for (var i in text) {
		var t = arr[text[i]];
        if(text[i] != undefined){
            if (t == undefined) {
                arr[text[i]] = {};
                t = {};
            }
        }
		if (i < text.length) {
            if(text[parseInt(i) + 1] != undefined){
                if(t[text[parseInt(i) + 1]] == undefined) arr[text[i]][text[parseInt(i) + 1]] = 1;
			    else arr[text[i]][text[parseInt(i) + 1]]++;
            }
		}
	}
}

